import React from "react";
import {
  View,
  Text,
  Image,
  StyleSheet,
  TouchableOpacity,
  TextInput,
} from "react-native";
import Ionicons from "react-native-vector-icons/Ionicons";

export default function CartProduct({ item }) {
  console.log(item);

  const removeProductHandler = (data) => {
    console.log(data);
  };

  return (
    <View style={styles.container}>
      <View style={styles.itemTop}>
        <Image
          style={styles.image}
          source={
            item?.thumb_img
              ? item.thumb_img.files.file
              : require("../../assets/images/notfound.jpg")
          }
        />
        <View style={styles.textContainer}>
          <Text style={styles.textTitle} numberOfLines={2} ellipsizeMode="tail">
            {item.name}
          </Text>
          <Text style={styles.textPrice}>{item.original_price}₾</Text>
        </View>
      </View>
      <View style={styles.itemBottom}>
        <TouchableOpacity
          style={styles.remove}
          onPress={() => removeProductHandler(item)}
        >
          <Ionicons name={"trash"} size={16} color={"tomato"} />
          წაშლა
        </TouchableOpacity>
        <View style={styles.qtyChange}>
          <TouchableOpacity style={styles.plus}>
            <Ionicons name={"remove"} size={16} color={"grey"} />
          </TouchableOpacity>
          <TextInput
            style={styles.textInput}
            keyboardType="numeric"
            value={0}
          />
          <TouchableOpacity style={styles.plus}>
            <Ionicons name={"add"} size={16} color={"grey"} />
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    display: "flex",
    borderBottomWidth: "1px",
    borderBottomColor: "#d5d5d5",
    color: "#092b43",
    position: "relative",
    padding: "15px",
    flexDirection: "column",
    backgroundColor: "white",
    overflow: "hidden",
  },
  itemTop: {
    display: "flex",
    flexDirection: "row",
    width: "100%",
  },
  itemBottom: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    width: "100%",
    marginTop: "15px",
  },
  image: {
    width: "30%",
    aspectRatio: "1 / 1",
    objectFit: "contain",
  },
  remove: {
    height: "40px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "white",
    color: "tomato",
  },
  textContainer: {
    display: "flex",
    flexDirection: "column",
    padding: "10px",
    width: "70%",
  },
  textTitle: {
    fontSize: "12px",
  },
  textPrice: {
    fontSize: "16px",
    fontWeight: "bold",
  },
  qtyChange: {
    display: "flex",
    alignItems: "center",
    flexDirection: "row",
  },
  textInput: {
    height: "28px",
    width: "35px",
    textAlign: "center",
  },
});
