import React from "react";
import { View, Text, Image, StyleSheet, TouchableOpacity } from "react-native";

export default function CartProduct({ item }) {
  console.log(item);

  return (
    <View style={styles.container}>
      <View style={styles.itemTop}>
        <Image
          style={styles.image}
          source={
            item?.thumb_img
              ? item.thumb_img.files.file
              : require("../../assets/images/notfound.jpg")
          }
        />
        <View style={styles.textContainer}>
          <Text style={styles.textTitle} numberOfLines={2} ellipsizeMode="tail">
            {item.name}
          </Text>
          <Text style={styles.textPrice}>{item.original_price}â‚¾</Text>
        </View>
      </View>
      <View style={styles.itemBottom}>
        <TouchableOpacity
          style={styles.remove}
          onPress={() => removeProductHandler(item)}
        >
          <Ionicons name={"cart"} size={16} color={"grey"} />
        </TouchableOpacity>
        <View>
          <TouchableOpacity
            style={styles.plus}
            onPress={() => addProductHandler(item)}
          >
            <Ionicons name={"minus"} size={16} color={"grey"} />
          </TouchableOpacity>
          <TextInput
            style={styles.textInput}
            keyboardType="numeric"
            onChangeText={(text) => this.onChanged(text)}
            value={this.state.myNumber}
          />
          <TouchableOpacity
            style={styles.plus}
            onPress={() => addProductHandler(item)}
          >
            <Ionicons name={"plus"} size={16} color={"grey"} />
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    display: "flex",
    borderBottomWidth: "1px",
    borderBottomColor: "#d5d5d5",
    color: "#092b43",
    position: "relative",
    margin: "15px",
    flexDirection: "row",
    backgroundColor: "white",
    overflow: "hidden",
  },
  image: {
    width: "30%",
    aspectRatio: "1 / 1",
    objectFit: "contain",
  },
  cart: {
    width: "40px",
    height: "40px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    borderRadius: "50%",
    backgroundColor: "white",
    border: "1px solid #dcdfe6",
    position: "absolute",
    right: "15px",
    bottom: "15px",
  },
  textContainer: {
    display: "flex",
    flexDirection: "column",
    padding: "10px",
    width: "70%",
  },
  textTitle: {
    fontSize: "12px",
  },
  textPrice: {
    fontSize: "16px",
    fontWeight: "bold",
  },
});
