import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import Auth from "./src/pages/Auth";
import Basket from "./src/pages/Basket";
import ProductsPage from "./src/pages/ProductsPage";
import Ionicons from "react-native-vector-icons/Ionicons";
import { Provider, useSelector } from "react-redux";
import { store } from "./src/redux/store.js";
import { PersistGate } from "redux-persist/integration/react";
import { persistStore } from "redux-persist";
import { getTotals } from "./src/redux/reducers/cartItems";
import { View, Text } from "react-native-web";

let persistor = persistStore(store);

const homeName = "მთავარი";
const basketName = "კალათა";
const authName = "პროფილი";

const Tab = createBottomTabNavigator();
export default function App() {
  const cart = useSelector((state) => state.cart);
  return (
    <Provider store={store}>
      <PersistGate loading={null} persistor={persistor}>
        <NavigationContainer>
          <Tab.Navigator
            initialRouteName={homeName}
            screenOptions={({ route }) => ({
              tabBarIcon: ({ focused, color, size }) => {
                let iconName;
                let rn = route.name;

                if (rn === homeName) {
                  iconName = focused ? "home" : "home-outline";
                } else if (rn === authName) {
                  iconName = focused ? "log-in" : "log-in-outline";
                } else if (rn === basketName) {
                  iconName = focused ? "cart" : "cart-outline";

                  return (
                    <View style={{ position: "relative" }}>
                      <Ionicons name={iconName} size={size} color={color} />
                      <Text
                        style={{
                          position: "absolute",
                          width: "15px",
                          height: "15px",
                          borderRadius: "50%",
                          top: "-3px",
                          right: "-3px",
                          whiteSpace: "nowrap",
                          backgroundColor: "tomato",
                          fontSize: "10px",
                          color: "white",
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "center",
                        }}
                      >
                        {cart.cartTotalQty}
                      </Text>
                    </View>
                  );
                }

                return <Ionicons name={iconName} size={size} color={color} />;
              },
              tabBarActiveTintColor: "#211844",
              tabBarInactiveTintColor: "grey",
              tabBarLabelStyle: { paddingBottom: 10, fontSize: 10 },
              tabBarStyle: { height: 60 },
            })}
          >
            <Tab.Screen name={authName} component={Auth} />
            <Tab.Screen name={homeName} component={ProductsPage} />
            <Tab.Screen name={basketName} component={Basket} />
          </Tab.Navigator>
        </NavigationContainer>
      </PersistGate>
    </Provider>
  );
}
